mat = [[1,2,3,4],[5,6,7,8],[9,1,2,3],[4,5,3,7]]c=0x=0y=0for i in range(0,4):    for j in range(0,3):        if j == 0:            if mat[i][j] == mat[i][j+1] or mat[i][j] == mat[i][j+2] or mat[i][j] == mat[i][j+3]:                c = c+1                break        if j == 1:            if mat[i][j] == mat[i][j+1] or mat[i][j] == mat[i][j+2]:                x = x+1                break        if j == 2:            if mat[i][j] == mat[i][j+1]:                y = y+1                break        if j == 3:            breakprint(c+x+y)#input matrix'''1 2 3 45 6 7 89 1 2 34 5 3 7'''#output = 0 aa raha hai